module.exports = {
    getExplication: function (fiche) {
        //console.log(fiche);
        var exp = generateHTMLExplication(fiche);
        return ('<div id="dExplication"  style="text-align: justify; padding: 10px">' + exp +'</div>');
    },

    getExplicationPlus: function (fiche) {
        //console.log(fiche);
        var exp = generateHTMLExplicationPlus(fiche);
        return ('<div id="dExplication"  style="text-align: justify; padding: 10px">' + exp +'</div>');
    }
}


